  --warning-orange: hsl(32.7 98% 60%);
  --danger-red: hsl(0 84.2% 60.2%);
  --info-blue: hsl(221.2 83.2% 53.3%);
  --light-bg: hsl(0 0% 98%);
  --dark-text: hsl(0 0% 20%);
  --glass-bg: hsla(0, 0%, 100%, 0.9);
  --glass-border: hsla(0, 0%, 100%, 0.2);
}

.dark {
  --background: hsl(0 0% 0%);
  --foreground: hsl(200 6.6667% 91.1765%);
  --card: hsl(228 9.8039% 10%);
  --card-foreground: hsl(0 0% 85.0980%);
  --popover: hsl(0 0% 0%);
  --popover-foreground: hsl(200 6.6667% 91.1765%);
  --primary: hsl(211.3 100% 50%);
  --primary-foreground: hsl(0 0% 100%);
  --secondary: hsl(195.0000 15.3846% 94.9020%);
  --secondary-foreground: hsl(210 25% 7.8431%);
  --muted: hsl(0 0% 9.4118%);
  --muted-foreground: hsl(210 3.3898% 46.2745%);
  --accent: hsl(205.7143 70% 7.8431%);
  --accent-foreground: hsl(203.7736 87.6033% 52.5490%);
  --destructive: hsl(0 84% 60%);
  --destructive-foreground: hsl(0 0% 100%);
  --border: hsl(210 5.2632% 14.9020%);
  --input: hsl(207.6923 27.6596% 18.4314%);
  --ring: hsl(202.8169 89.1213% 53.1373%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    background: var(--light-bg);
    color: var(--dark-text);
    font-family: 'Inter', sans-serif;
    line-height: 1.6;
  }
}

/* Wrap My Kitchen Layout */
.app-layout {
  display: flex;
  min-height: 100vh;
}

/* Modern Sidebar Styles */
.modern-sidebar {
  width: 280px;
  background: linear-gradient(180deg, #ffffff 0%, #fafafa 100%);
  border-right: 1px solid #e5e7eb;
  display: flex;
  flex-direction: column;
  position: fixed;
  height: 100vh;
  z-index: 1000;
  transition: width 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  backdrop-filter: blur(8px);
  box-shadow: 0 0 32px rgba(0, 0, 0, 0.08);
}

.modern-sidebar.collapsed {
  width: 80px;
}

/* Header */
.sidebar-header-modern {
  padding: 1.5rem;
  border-bottom: 1px solid #f1f5f9;
  display: flex;
  align-items: center;
  justify-content: space-between;
  background: linear-gradient(135deg, #ffffff 0%, #f8fafc 100%);
}

.sidebar-brand-modern {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  text-decoration: none;
  transition: all 0.2s ease;
}

.brand-logo {
  width: 48px;
  height: 48px;
  border-radius: 12px;
  background: linear-gradient(135deg, var(--wmk-green) 0%, var(--wmk-green-dark) 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 4px 12px rgba(77, 179, 102, 0.2);
  overflow: hidden;
}

.logo-image {
  width: 32px;
  height: 32px;
  object-fit: contain;
}

.brand-text {
  opacity: 1;
  transition: opacity 0.2s ease;
}

.brand-title {
  font-size: 1.125rem;
  font-weight: 700;
  color: var(--wmk-black);
  margin: 0;
  line-height: 1.2;
}

.brand-subtitle {
  font-size: 0.75rem;
  color: var(--wmk-gray);
  margin: 0;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.collapse-toggle {
  width: 32px;
  height: 32px;
  border: none;
  background: #f1f5f9;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: var(--wmk-gray);
  transition: all 0.2s ease;
}

.collapse-toggle:hover {
  background: #e2e8f0;
  color: var(--wmk-black);
}

/* Navigation */
.sidebar-nav-modern {
  flex: 1;
  padding: 1rem 0;
  overflow-y: auto;
  scrollbar-width: none;
  -ms-overflow-style: none;
}

.sidebar-nav-modern::-webkit-scrollbar {
  display: none;
}

.nav-section {
  margin-bottom: 2rem;
}

.nav-section-title {
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--wmk-gray);
  text-transform: uppercase;
  letter-spacing: 0.05em;
  padding: 0 1.5rem;
  margin-bottom: 0.75rem;
}

.nav-item-modern {
  display: block;
  text-decoration: none;
  margin: 0.25rem 1rem;
  border-radius: 12px;
  position: relative;
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

.nav-item-modern:hover {
  transform: translateX(4px);
}

.nav-item-content {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 0.875rem 1rem;
  color: var(--wmk-black);
  position: relative;
}

.nav-icon {
  color: var(--wmk-gray);
  transition: color 0.2s ease;
  flex-shrink: 0;
}

.nav-label {
  font-weight: 500;
  font-size: 0.875rem;
  opacity: 1;
  transition: opacity 0.2s ease;
}

.modern-sidebar.collapsed .nav-label {
  opacity: 0;
  pointer-events: none;
}

.nav-item-modern:hover .nav-icon {
  color: var(--wmk-green);
}

.nav-item-modern:hover .nav-item-content {
  background: linear-gradient(135deg, #f0fdf4 0%, #dcfce7 100%);
  color: var(--wmk-black);
}

.nav-item-modern.active .nav-item-content {
  background: linear-gradient(135deg, var(--wmk-green) 0%, var(--wmk-green-dark) 100%);
  color: white;
  box-shadow: 0 4px 16px rgba(77, 179, 102, 0.3);
}

.nav-item-modern.active .nav-icon,
.nav-item-modern.active .nav-label {
  color: white;
}

.active-indicator {
  position: absolute;
  right: 0;
  top: 50%;
  transform: translateY(-50%);
  width: 4px;
  height: 24px;
  background: white;
  border-radius: 2px 0 0 2px;
}

/* Footer */
.sidebar-footer-modern {
  padding: 1.5rem;
  border-top: 1px solid #f1f5f9;
  background: linear-gradient(135deg, #ffffff 0%, #f8fafc 100%);
}

.user-profile {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  margin-bottom: 1rem;
  padding: 0.75rem;
  background: linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%);
  border-radius: 12px;
  transition: all 0.2s ease;
}

.user-avatar-modern {
  width: 40px;
  height: 40px;
  border-radius: 10px;
  background: linear-gradient(135deg, var(--wmk-green) 0%, var(--wmk-green-dark) 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 600;
  font-size: 0.875rem;
  box-shadow: 0 2px 8px rgba(77, 179, 102, 0.2);
  flex-shrink: 0;
}

.user-info {
  flex: 1;
  min-width: 0;
  opacity: 1;
  transition: opacity 0.2s ease;
}

.modern-sidebar.collapsed .user-info {
  opacity: 0;
  pointer-events: none;
}

.user-name {
  font-size: 0.875rem;
  font-weight: 600;
  color: var(--wmk-black);
  margin: 0;
  line-height: 1.2;
}

.user-role {
  font-size: 0.75rem;
  color: var(--wmk-gray);
  margin: 0;
  line-height: 1.2;
}

.logout-btn-modern {
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  padding: 0.75rem;
  background: linear-gradient(135deg, #fef2f2 0%, #fee2e2 100%);
  border: 1px solid #fecaca;
  border-radius: 12px;
  color: #dc2626;
  font-weight: 500;
  font-size: 0.875rem;
  cursor: pointer;
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

.logout-btn-modern:hover {
  background: linear-gradient(135deg, #dc2626 0%, #b91c1c 100%);
  color: white;
  transform: translateY(-1px);
  box-shadow: 0 4px 16px rgba(220, 38, 38, 0.3);
}

.modern-sidebar.collapsed .logout-btn-modern span {
  display: none;
}

/* Legacy sidebar compatibility */
.sidebar {
