        await db.insert(leads).values(leadData);
        leadsImported++;

        if (leadsImported % 50 === 0) {
          console.log(`Imported ${leadsImported} leads...`);
        }
      } catch (error) {
        console.error(`Error importing lead: ${error.message}`);
        console.error(`Row data: ${row.substring(0, 100)}...`);
        leadsSkipped++;
      }
    }

    console.log('\n=== IMPORT COMPLETE ===');
    console.log(`Leads imported: ${leadsImported}`);
    console.log(`Leads skipped: ${leadsSkipped}`);
    console.log(`Total records imported: ${leadsImported}`);

  } catch (error) {
    console.error('Error during import:', error);
    throw error;
  } finally {
    await connection.end();
  }
}

// Run the import
importFullData()
  .then(() => {
    console.log('Full data import completed successfully!');
    process.exit(0);
  })
  .catch((error) => {
    console.error('Import failed:', error);
    process.exit(1);
  });

import-sample-booklets.js.content:
import fs from 'fs';
import { drizzle } from 'drizzle-orm/mysql2';
import mysql from 'mysql2/promise';
import { randomUUID } from 'crypto';

// Database configuration
const connection = mysql.createPool({
  host: 'localhost',
  user: 'root',
  password: '',
  database: 'wmk_crm',
  waitForConnections: true,
  connectionLimit: 10,
  queueLimit: 0,
});

const db = drizzle(connection);

// Import the schema
import { sampleBooklets } from './shared/schema.js';

// Mapping functions
function mapProductType(productType) {
  const mapping = {
    'Sample Booklet Only': 'sample_booklet_only',
    'Demo Kit and Sample Booklet': 'demo_kit_and_sample_booklet',
    'Trial Kit': 'trial_kit',
    'Demo Kit Only': 'demo_kit_only'
  };
  return mapping[productType] || 'sample_booklet_only';
}

function mapStatus(status) {
  const mapping = {
    'Pending': 'pending',
    'Shipped': 'shipped',
    'Delivered': 'delivered'
  };
  return mapping[status] || 'pending';
}

// Function to convert date format
function convertDate(dateStr) {
  if (!dateStr || dateStr === 'NULL' || dateStr === null) return null;

  // Handle string dates
  if (typeof dateStr === 'string') {
    // Remove quotes if present
    dateStr = dateStr.replace(/^['"]|['"]$/g, '');

    // Try to parse the date
    try {
      const parsed = new Date(dateStr);
      if (!isNaN(parsed.getTime())) {
        return parsed; // Return Date object
      }
    } catch (e) {
      // If parsing fails, return null
      return null;
    }
  }

  return null;
}

// Function to parse a single row of values
function parseRowValues(valuesString) {
  const values = [];
  let current = '';
  let inQuotes = false;
  let quoteChar = '';

  for (let i = 0; i < valuesString.length; i++) {
    const char = valuesString[i];
    const prevChar = i > 0 ? valuesString[i - 1] : '';

    if ((char === '"' || char === "'") && prevChar !== '\\') {
      if (!inQuotes) {
        inQuotes = true;
        quoteChar = char;
      } else if (char === quoteChar) {
        inQuotes = false;
        quoteChar = '';
      }
    }

    if (!inQuotes && char === ',') {
      values.push(current.trim());
      current = '';
      continue;
    }

    current += char;
  }

  if (current.trim()) {
    values.push(current.trim());
  }

  // Clean up values
  return values.map(val => {
    val = val.trim();

    // Handle NULL
    if (val === 'NULL') return null;

    // Handle quoted strings
    if ((val.startsWith("'") && val.endsWith("'")) || 
        (val.startsWith('"') && val.endsWith('"'))) {
      return val.slice(1, -1).replace(/\\'/g, "'").replace(/\\"/g, '"').replace(/\\\\/g, '\\');
    }

    return val;
  });
}

// Function to split INSERT statement into individual rows
function splitIntoRows(valuesString) {
  const rows = [];
  let currentRow = '';
  let parenLevel = 0;
  let inQuotes = false;
  let quoteChar = '';

  for (let i = 0; i < valuesString.length; i++) {
    const char = valuesString[i];
    const prevChar = i > 0 ? valuesString[i - 1] : '';

    if ((char === '"' || char === "'") && prevChar !== '\\') {
      if (!inQuotes) {
        inQuotes = true;
        quoteChar = char;
      } else if (char === quoteChar) {
        inQuotes = false;
        quoteChar = '';
      }
    }

    if (!inQuotes) {
      if (char === '(') {
        parenLevel++;
        if (parenLevel === 1) {
          currentRow = ''; // Start new row
          continue;
        }
      } else if (char === ')') {
        parenLevel--;
        if (parenLevel === 0) {
          // End of current row
          if (currentRow.trim()) {
            rows.push(currentRow.trim());
          }
          currentRow = '';
          continue;
        }
      }
    }

    if (parenLevel > 0) {
      currentRow += char;
    }
  }

  return rows;
}

async function importSampleBooklets() {
  try {
    console.log('Reading SQL file...');
    const sqlContent = fs.readFileSync('./wrapqrqc_wmk.sql', 'utf8');

    // Find the sample booklets INSERT statement
    const insertMatch = sqlContent.match(/INSERT INTO `sample_booklets`[\s\S]*?;/i);

    if (!insertMatch) {
      console.log('No INSERT statement found for sample_booklets');
      return;
    }

    console.log('Found sample booklets INSERT statement');

    const insertStatement = insertMatch[0];

    // Extract the VALUES part
    const valuesMatch = insertStatement.match(/VALUES\s+([\s\S]*?);/i);
    if (!valuesMatch) {
      console.log('Could not extract VALUES section');
      return;
    }

    let valuesString = valuesMatch[1].trim();
    const rows = splitIntoRows(valuesString);

    console.log(`Found ${rows.length} sample booklets to import`);

    // Clear existing data first
    console.log('Clearing existing sample booklets data...');
    await connection.execute('DELETE FROM sample_booklets');

    let imported = 0;
    let skipped = 0;

    // Import sample booklets
    for (let rowIndex = 0; rowIndex < rows.length; rowIndex++) {
      try {
        const row = rows[rowIndex];
        const values = parseRowValues(row);

        if (values.length < 12) {
          console.log(`Row ${rowIndex + 1}: Skipping - insufficient columns (${values.length})`);
          skipped++;
          continue;
        }

        // Map the values to our schema
        const bookletData = {
          id: randomUUID(),
          order_number: values[1] || '',
          customer_name: values[2] || '',
          address: values[3] || '',
          email: values[4] || '',
          phone: values[5] || '',
          product_type: mapProductType(values[6]),
          tracking_number: values[7],
          status: mapStatus(values[8]),
          date_ordered: convertDate(values[9]) || new Date(),
          date_shipped: convertDate(values[10]),
          notes: values[11] || '',
          created_at: convertDate(values[12]) || new Date(),
          updated_at: convertDate(values[13]) || new Date(),
        };

        // Insert the sample booklet
        await db.insert(sampleBooklets).values(bookletData);
        imported++;

        console.log(`Imported sample booklet ${imported}: ${bookletData.customer_name} (${bookletData.order_number})`);

      } catch (error) {
        console.error(`Error importing row ${rowIndex + 1}: ${error.message}`);
        skipped++;
      }
    }

    console.log('\n=== Sample Booklets Import Complete ===');
    console.log(`Total imported: ${imported}`);
    console.log(`Total skipped: ${skipped}`);

  } catch (error) {
    console.error('Error during import:', error);
  } finally {
    await connection.end();
  }
}

// Run the import
importSampleBooklets();

verify-import.js.content:
import mysql from 'mysql2/promise';

async function verifyImport() {
  try {
    const connection = await mysql.createConnection({
      host: 'localhost',
      user: 'root',
      password: '',
      database: 'wmk_crm'
    });

    // Count total leads
    const [countRows] = await connection.execute('SELECT COUNT(*) as total FROM leads');
    console.log('Total leads in database:', countRows[0].total);

    // Get sample data
    const [sampleRows] = await connection.execute(`
      SELECT name, phone, email, lead_origin, remarks, assigned_to 
      FROM leads 
      ORDER BY date_created DESC
      LIMIT 10
    `);

    console.log('\nSample leads (latest 10):');
    console.table(sampleRows);

    // Check lead origins distribution
    const [originRows] = await connection.execute(`
      SELECT lead_origin, COUNT(*) as count 
      FROM leads 
      GROUP BY lead_origin 
      ORDER BY count DESC
    `);

    console.log('\nLead origins distribution:');
    console.table(originRows);

    // Check status distribution (remarks field)
    const [statusRows] = await connection.execute(`
      SELECT remarks, COUNT(*) as count 
      FROM leads 
      GROUP BY remarks 
      ORDER BY count DESC
    `);

    console.log('\nStatus distribution (remarks field):');
    console.table(statusRows);

    await connection.end();
  } catch (error) {
    console.error('Error:', error);
  }
}

verifyImport();

server/email-service.ts.content:
import nodemailer from 'nodemailer';

interface EmailOptions {
  to: string;
  subject: string;
  text: string;
  html?: string;
}

class EmailService {
  private transporter!: nodemailer.Transporter;
  private isInitialized = false;

  constructor() {
    this.initializeTransporter();
  }

  private async initializeTransporter() {
    try {
      // Check if real SMTP credentials are provided
      if (process.env.EMAIL_USER && process.env.EMAIL_PASS) {
        // Use real SMTP configuration
        this.transporter = nodemailer.createTransport({
          host: process.env.SMTP_HOST || 'smtp.gmail.com',
          port: parseInt(process.env.SMTP_PORT || '587'),
          secure: false, // true for 465, false for other ports
          auth: {
            user: process.env.EMAIL_USER,
            pass: process.env.EMAIL_PASS
          }
        });
        console.log('Email service initialized with real SMTP credentials');
      } else {
        // Use Ethereal Email for testing
        const testAccount = await nodemailer.createTestAccount();
        this.transporter = nodemailer.createTransport({
          host: 'smtp.ethereal.email',
          port: 587,
          secure: false,
          auth: {
            user: testAccount.user,
            pass: testAccount.pass
          }
        });
        console.log('Email service initialized with Ethereal Email (testing mode)');
        console.log('Test account:', testAccount.user);
      }
      this.isInitialized = true;
    } catch (error) {
      console.error('Failed to initialize email service:', error);
      // Fallback to a basic configuration
      this.transporter = nodemailer.createTransport({
        streamTransport: true,
        newline: 'unix',
        buffer: true
      });
    }
  }

  async sendEmail(options: EmailOptions): Promise<void> {
    try {
      // Ensure transporter is initialized
      if (!this.isInitialized) {
        await this.initializeTransporter();
      }

      // Use provided HTML or convert plain text to HTML if needed
      const htmlContent = options.html || this.textToHtml(options.text);

      const mailOptions = {
        from: process.env.EMAIL_FROM || '"WMK Installation Team" <noreply@wmk-crm.com>',
        to: options.to,
        subject: options.subject,
        text: options.text,
        html: htmlContent
      };

      const info = await this.transporter.sendMail(mailOptions);
      console.log('Email sent successfully:', info.messageId);

      // For Ethereal Email, log the preview URL
      if (!process.env.EMAIL_USER || !process.env.EMAIL_PASS) {
        const previewUrl = nodemailer.getTestMessageUrl(info);
        if (previewUrl) {
          console.log('📧 Email preview URL:', previewUrl);
        }
      }
    } catch (error) {
      console.error('Failed to send email:', error);
      throw new Error('Failed to send email notification');
    }
  }

  private textToHtml(text: string): string {
    // Convert professional plain text to styled HTML
    return text
      // Convert headers with box borders
      .replace(/┌─+┐\n│\s*(.*?)\s*│\n└─+┘/g, '<div style="background:#f8f9fa;border:2px solid #dee2e6;border-radius:8px;padding:12px;margin:16px 0;text-align:center;"><h3 style="margin:0;color:#495057;font-size:14px;font-weight:bold;text-transform:uppercase;letter-spacing:1px;">$1</h3></div>')

      // Convert checkboxes
      .replace(/□\s*(.*?)$/gm, '<div style="margin:4px 0;"><span style="color:#6c757d;">☐</span> $1</div>')
      .replace(/✓\s*(.*?)$/gm, '<div style="margin:4px 0;color:#28a745;"><span style="font-weight:bold;">✓</span> $1</div>')

      // Convert status indicators
      .replace(/✅\s*(.*?)$/gm, '<span style="color:#28a745;font-weight:bold;">✅ $1</span>')
      .replace(/⚠️\s*(.*?)$/gm, '<span style="color:#fd7e14;font-weight:bold;">⚠️ $1</span>')

      // Convert bullet points with icons
      .replace(/•\s*(.*?)$/gm, '<div style="margin:4px 0;"><span style="color:#007bff;">•</span> $1</div>')

      // Convert contact info
      .replace(/📞\s*(.*?)$/gm, '<div style="margin:4px 0;"><span style="color:#17a2b8;">📞</span> $1</div>')
      .replace(/📧\s*(.*?)$/gm, '<div style="margin:4px 0;"><span style="color:#17a2b8;">📧</span> $1</div>')
      .replace(/🌐\s*(.*?)$/gm, '<div style="margin:4px 0;"><span style="color:#17a2b8;">🌐</span> $1</div>')
      .replace(/📅\s*(.*?)$/gm, '<div style="margin:8px 0;"><span style="color:#007bff;font-weight:bold;">📅</span> $1</div>')
      .replace(/👤\s*(.*?)$/gm, '<div style="margin:8px 0;"><span style="color:#6f42c1;font-weight:bold;">👤</span> $1</div>')
      .replace(/🔧\s*(.*?)$/gm, '<div style="margin:8px 0;"><span style="color:#fd7e14;font-weight:bold;">🔧</span> $1</div>')
      .replace(/💰\s*(.*?)$/gm, '<div style="margin:8px 0;"><span style="color:#28a745;font-weight:bold;">💰</span> $1</div>')

      // Convert double line breaks to paragraphs
      .replace(/\n\n/g, '</p><p style="margin:12px 0;line-height:1.5;">')

      // Convert single line breaks to br tags
      .replace(/\n/g, '<br>')

      // Wrap in paragraphs
      .replace(/^/, '<div style="font-family:Arial,sans-serif;font-size:14px;line-height:1.6;color:#333;"><p style="margin:12px 0;line-height:1.5;">')
      .replace(/$/, '</p></div>')

      // Bold text
      .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
      .replace(/\*(.*?)\*/g, '<em>$1</em>')

      // Style company signature
      .replace(/(Quality • Craftsmanship • Excellence|Excellence in Every Installation)/, '<div style="margin-top:20px;padding:12px;background:#f8f9fa;border-left:4px solid #007bff;font-style:italic;color:#6c757d;text-align:center;">$1</div>');
  }

  async testConnection(): Promise<boolean> {
    try {
      if (!this.isInitialized) {
        await this.initializeTransporter();
      }
      await this.transporter.verify();
      console.log('SMTP connection verified successfully');
      return true;
    } catch (error) {
      console.error('SMTP connection failed:', error);
      return false;
    }
  }
}

export const emailService = new EmailService();

