                      {enrichmentData.jobTitle && (
                        <div className="flex items-center gap-2">
                          <Users className="h-4 w-4 text-gray-500" />
                          <span className="font-medium">Job Title:</span>
                          <span>{enrichmentData.jobTitle}</span>
