import { useState } from 'react';
import { useAuth } from '@/contexts/auth-context';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Checkbox } from '@/components/ui/checkbox';
import { Switch } from '@/components/ui/switch';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { 
  Users, 
  Plus, 
  Edit, 
  Trash2, 
  ArrowLeft,
  Eye,
  EyeOff,
  Shield,
  User,
  Settings,
  Key
} from 'lucide-react';
import { Redirect } from 'wouter';

// Define user role hierarchy (higher number = higher authority)
const ROLE_HIERARCHY = {
  'installer': 1,
  'sales_rep': 2,
  'manager': 3,
  'owner': 4,
  'admin': 5,
  'administrator': 5
} as const;

// Define all available permissions
const AVAILABLE_PERMISSIONS = [
  { id: 'dashboard', label: 'Dashboard', description: 'View main dashboard and statistics', minRole: 1 },
  { id: 'leads', label: 'Leads Management', description: 'View, create, edit and manage leads', minRole: 2 },
  { id: 'followups', label: 'Follow-ups', description: 'View and manage follow-up tasks', minRole: 2 },
  { id: 'installations', label: 'Installations', description: 'View and manage installation schedules', minRole: 1 },
  { id: 'installers', label: 'Installers Management', description: 'Manage installer personnel and assignments', minRole: 3 },
  { id: 'sample-booklets', label: 'Sample Booklets', description: 'Manage sample booklet orders', minRole: 2 },
  { id: 'reports', label: 'Reports & Analytics', description: 'View reports and business analytics', minRole: 3 },
  { id: 'admin', label: 'Admin Panel', description: 'Access administrative functions', minRole: 4 },
  { id: 'user-management', label: 'User Management', description: 'Create and manage user accounts', minRole: 4 },
  { id: 'system-settings', label: 'System Settings', description: 'Configure system-wide settings', minRole: 5 },
] as const;

interface User {
  id: string;
  username: string;
  name?: string;
  email?: string;
  role: string;
  permissions?: string[];
  is_active?: boolean;
  created_at?: string;
  last_login?: string;
}

export default function UserManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [editingUser, setEditingUser] = useState<User | null>(null);
  const [showPassword, setShowPassword] = useState(false);
  const [resetPasswordUser, setResetPasswordUser] = useState<string | null>(null);
  const [newPassword, setNewPassword] = useState('');
  const [newUser, setNewUser] = useState({
    username: '',
    name: '',
    email: '',
    password: '',
    role: 'sales_rep',
    permissions: [] as string[],
    is_active: true
  });

  // Redirect non-admin users
  if (!user || (user.role !== 'admin' && user.role !== 'administrator')) {
    return <Redirect to="/dashboard" />;
  }

  const { data: users = [], isLoading } = useQuery<User[]>({
    queryKey: ['/api/admin/users'],
  });

  const createUserMutation = useMutation({
    mutationFn: async (userData: typeof newUser) => {
      const response = await apiRequest('POST', '/api/admin/users', userData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/users'] });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/dashboard-stats'] });
      setIsCreateDialogOpen(false);
      setNewUser({ username: '', name: '', email: '', password: '', role: 'sales_rep', permissions: [], is_active: true });
      toast({ title: "User created successfully", variant: "default" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Failed to create user", 
        description: error.message || "Please try again",
        variant: "destructive" 
      });
    }
  });

  const updateUserMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<User> }) => {
      const response = await apiRequest('PUT', `/api/admin/users/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/users'] });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/dashboard-stats'] });
      setEditingUser(null);
      toast({ title: "User updated successfully", variant: "default" });
    },
    onError: () => toast({ title: "Failed to update user", variant: "destructive" })
  });

  const deleteUserMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await apiRequest('DELETE', `/api/admin/users/${id}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/users'] });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/dashboard-stats'] });
      toast({ title: "User deleted successfully", variant: "default" });
    },
    onError: () => toast({ title: "Failed to delete user", variant: "destructive" })
  });

  const resetPasswordMutation = useMutation({
    mutationFn: async ({ userId, password }: { userId: string; password: string }) => {
      const response = await apiRequest('POST', `/api/admin/users/${userId}/reset-password`, { password });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/users'] });
      setResetPasswordUser(null);
      setNewPassword('');
      toast({ title: "Password reset successfully", variant: "default" });
    },
    onError: () => toast({ title: "Failed to reset password", variant: "destructive" })
  });

  const handleCreateUser = () => {
    if (!newUser.username || !newUser.password) {
      toast({ title: "Username and password are required", variant: "destructive" });
      return;
    }
    createUserMutation.mutate(newUser);
  };

  const handlePermissionToggle = (permission: string, isEditing = false) => {
    if (isEditing && editingUser) {
      const currentPermissions = editingUser.permissions || [];
      const newPermissions = currentPermissions.includes(permission)
        ? currentPermissions.filter(p => p !== permission)
        : [...currentPermissions, permission];

      setEditingUser({ ...editingUser, permissions: newPermissions });
    } else {
      const newPermissions = newUser.permissions.includes(permission)
        ? newUser.permissions.filter(p => p !== permission)
        : [...newUser.permissions, permission];

      setNewUser({ ...newUser, permissions: newPermissions });
    }
  };

  const handleRoleChange = (role: string, isEditing = false) => {
    const roleLevel = ROLE_HIERARCHY[role as keyof typeof ROLE_HIERARCHY];
    // Auto-assign permissions based on role hierarchy
    const defaultPermissions = AVAILABLE_PERMISSIONS
      .filter(p => roleLevel >= p.minRole)
      .map(p => p.id);

    if (isEditing && editingUser) {
      setEditingUser({ ...editingUser, role, permissions: defaultPermissions });
    } else {
      setNewUser({ ...newUser, role, permissions: defaultPermissions });
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/3 mb-2"></div>
          <div className="h-4 bg-gray-200 rounded w-1/2"></div>
        </div>
        <div className="h-64 bg-gray-200 rounded animate-pulse"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-2">
            <Button variant="outline" size="sm" onClick={() => window.location.href = '/admin'}>
              <ArrowLeft className="h-4 w-4" />
            </Button>
            <Users className="h-8 w-8 text-blue-600" />
            User Management
          </h1>
          <p className="text-gray-600 mt-1">Manage user accounts and their permissions</p>
        </div>
        <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
          <DialogTrigger asChild>
            <Button className="flex items-center gap-2" data-testid="button-create-user">
              <Plus className="h-4 w-4" />
              Create User
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-3xl">
            <DialogHeader>
              <DialogTitle>Create New User</DialogTitle>
              <DialogDescription>
                Create a new user account with role-based permissions
              </DialogDescription>
            </DialogHeader>
            <div className="space-y-6">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="username">Username *</Label>
                  <Input
                    id="username"
                    value={newUser.username}
                    onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
                    placeholder="Enter username"
                    data-testid="input-username"
                  />
                </div>
                <div>
                  <Label htmlFor="name">Full Name</Label>
                  <Input
                    id="name"
                    value={newUser.name}
                    onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
                    placeholder="Enter full name"
                    data-testid="input-name"
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    type="email"
                    value={newUser.email}
                    onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                    placeholder="Enter email address"
                    data-testid="input-email"
                  />
                </div>
                <div>
                  <Label htmlFor="password">Password *</Label>
                  <div className="relative">
                    <Input
                      id="password"
                      type={showPassword ? "text" : "password"}
                      value={newUser.password}
                      onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
                      placeholder="Enter password"
                      data-testid="input-password"
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      className="absolute right-0 top-0 h-full px-3"
                      onClick={() => setShowPassword(!showPassword)}
                    >
                      {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                    </Button>
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="role">Role</Label>
                  <Select value={newUser.role} onValueChange={(value) => handleRoleChange(value)}>
                    <SelectTrigger data-testid="select-role">
                      <SelectValue placeholder="Select role" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="installer">Installer</SelectItem>
                      <SelectItem value="sales_rep">Sales Representative</SelectItem>
                      <SelectItem value="manager">Manager</SelectItem>
                      <SelectItem value="owner">Owner</SelectItem>
                      <SelectItem value="admin">Administrator</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div className="flex items-center space-x-2 pt-6">
                  <Switch
                    id="is_active"
                    checked={newUser.is_active}
                    onCheckedChange={(checked) => setNewUser({ ...newUser, is_active: checked })}
                    data-testid="switch-active"
                  />
                  <Label htmlFor="is_active">Active User</Label>
                </div>
              </div>

              <div>
                <Label className="text-base font-semibold">Page Permissions</Label>
                <p className="text-sm text-gray-600 mb-3">Select which pages this user can access</p>
                <div className="grid grid-cols-2 gap-3">
                  {AVAILABLE_PERMISSIONS.map((permission) => (
                    <div key={permission.id} className="flex items-start space-x-2 p-3 border rounded-lg">
                      <Checkbox
                        id={permission.id}
                        checked={newUser.permissions.includes(permission.id)}
                        onCheckedChange={() => handlePermissionToggle(permission.id)}
                        disabled={newUser.role === 'admin' || newUser.role === 'owner'} // Admins and Owners get all permissions
                        data-testid={`checkbox-${permission.id}`}
                      />
                      <div className="flex-1">
                        <Label htmlFor={permission.id} className="font-medium cursor-pointer">
                          {permission.label}
                        </Label>
                        <p className="text-xs text-gray-500">{permission.description}</p>
                      </div>
                    </div>
                  ))}
                </div>
                {(newUser.role === 'admin' || newUser.role === 'owner') && (
                  <p className="text-sm text-blue-600 mt-2">
                    <Shield className="h-4 w-4 inline mr-1" />
                    {newUser.role === 'admin' ? 'Administrators' : 'Owners'} have access to all pages by default
                  </p>
                )}
                {newUser.role && newUser.role !== 'admin' && newUser.role !== 'owner' && (
                  <p className="text-sm text-gray-600 mt-2">
                    Permissions automatically assigned based on role hierarchy. You can customize them below.
                  </p>
                )}
              </div>

              <div className="flex justify-end space-x-2">
                <Button variant="outline" onClick={() => setIsCreateDialogOpen(false)}>
                  Cancel
                </Button>
                <Button 
                  onClick={handleCreateUser} 
                  disabled={createUserMutation.isPending}
                  data-testid="button-save-user"
                >
                  {createUserMutation.isPending ? 'Creating...' : 'Create User'}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>System Users</CardTitle>
          <CardDescription>Manage all user accounts and their access permissions</CardDescription>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>User</TableHead>
                <TableHead>Role</TableHead>
                <TableHead>Permissions</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Last Login</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {users.map((user: User) => (
                <TableRow key={user.id} data-testid={`row-user-${user.id}`}>
                  <TableCell>
                    <div className="flex items-center gap-2">
                      <User className="h-4 w-4 text-gray-500" />
                      <div>
                        <div className="font-medium">{user.name || user.username}</div>
                        <div className="text-xs text-gray-500">@{user.username}</div>
                        {user.email && <div className="text-xs text-blue-600">{user.email}</div>}
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant={
                      user.role === 'admin' ? 'destructive' : 
                      user.role === 'owner' ? 'destructive' :
                      user.role === 'manager' ? 'default' :
                      'secondary'
                    }>
                      {user.role === 'admin' && <><Shield className="h-3 w-3 mr-1" />Administrator</>}
                      {user.role === 'owner' && <><Key className="h-3 w-3 mr-1" />Owner</>}
                      {user.role === 'manager' && <><Settings className="h-3 w-3 mr-1" />Manager</>}
                      {user.role === 'sales_rep' && <><User className="h-3 w-3 mr-1" />Sales Rep</>}
                      {user.role === 'installer' && <><User className="h-3 w-3 mr-1" />Installer</>}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <div className="flex flex-wrap gap-1">
                      {(user.role === 'admin' || user.role === 'owner') ? (
                        <Badge variant="outline" className="text-xs">All Permissions</Badge>
                      ) : (
                        (user.permissions || []).slice(0, 2).map(perm => (
                          <Badge key={perm} variant="outline" className="text-xs">
                            {AVAILABLE_PERMISSIONS.find(p => p.id === perm)?.label || perm}
                          </Badge>
                        ))
                      )}
                      {user.permissions && user.permissions.length > 2 && user.role !== 'admin' && user.role !== 'owner' && (
                        <Badge variant="outline" className="text-xs">
                          +{user.permissions.length - 2} more
                        </Badge>
                      )}
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant={user.is_active ? 'default' : 'secondary'}>
                      {user.is_active ? 'Active' : 'Inactive'}
                    </Badge>
                  </TableCell>
                  <TableCell className="text-sm text-gray-500">
                    {user.last_login ? new Date(user.last_login).toLocaleDateString() : 'Never'}
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-2">
                      <Button 
                        variant="outline" 
                        size="sm"
                        onClick={() => setEditingUser(user)}
                        data-testid={`button-edit-${user.id}`}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                      {user.username !== 'admin' && ( // Don't allow deleting admin user
                        <Button 
                          variant="outline" 
                          size="sm" 
                          onClick={() => deleteUserMutation.mutate(user.id)}
                          data-testid={`button-delete-${user.id}`}
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      )}
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* Edit User Dialog */}
      <Dialog open={!!editingUser} onOpenChange={() => setEditingUser(null)}>
        <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <Edit className="h-5 w-5" />
              Edit User Account
            </DialogTitle>
            <DialogDescription>
              Modify user information, role, and permissions for {editingUser?.username}
            </DialogDescription>
          </DialogHeader>
          {editingUser && (
            <div className="space-y-8">

              {/* User Information Section */}
              <div className="bg-gray-50 p-6 rounded-lg">
                <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
                  <User className="h-5 w-5" />
                  User Information
                </h3>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="edit-username">Username</Label>
                    <Input
                      id="edit-username"
                      value={editingUser.username}
                      onChange={(e) => setEditingUser({ ...editingUser, username: e.target.value })}
                      placeholder="Enter username"
                      data-testid="input-edit-username"
                    />
                  </div>
                  <div>
                    <Label htmlFor="edit-name">Full Name</Label>
                    <Input
                      id="edit-name"
                      value={editingUser.name || ''}
                      onChange={(e) => setEditingUser({ ...editingUser, name: e.target.value })}
                      placeholder="Enter full name"
                      data-testid="input-edit-name"
                    />
                  </div>
                </div>
                <div className="grid grid-cols-2 gap-4 mt-4">
                  <div>
                    <Label htmlFor="edit-email">Email</Label>
                    <Input
                      id="edit-email"
                      type="email"
                      value={editingUser.email || ''}
                      onChange={(e) => setEditingUser({ ...editingUser, email: e.target.value })}
                      placeholder="Enter email address"
                      data-testid="input-edit-email"
                    />
                  </div>
                  <div className="flex items-center space-x-3 pt-6">
                    <Switch
                      checked={editingUser.is_active}
                      onCheckedChange={(checked) => setEditingUser({ ...editingUser, is_active: checked })}
                      data-testid="switch-edit-active"
                    />
                    <Label className="text-sm font-medium">
                      {editingUser.is_active ? 'Active User' : 'Inactive User'}
                    </Label>
                    <Badge variant={editingUser.is_active ? 'default' : 'secondary'} className="ml-2">
                      {editingUser.is_active ? 'Active' : 'Inactive'}
                    </Badge>
                  </div>
                </div>
              </div>

              {/* Role & Access Section */}
              <div className="bg-blue-50 p-6 rounded-lg">
                <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
                  <Shield className="h-5 w-5" />
                  Role & Access Control
                </h3>
                <div className="mb-4">
                  <Label htmlFor="edit-role">User Role</Label>
                  <Select 
                    value={editingUser.role} 
                    onValueChange={(value) => handleRoleChange(value, true)}
                  >
                    <SelectTrigger data-testid="select-edit-role">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="installer">Installer</SelectItem>
                      <SelectItem value="sales_rep">Sales Representative</SelectItem>
                      <SelectItem value="manager">Manager</SelectItem>
                      <SelectItem value="owner">Owner</SelectItem>
                      <SelectItem value="admin">Administrator</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label className="text-base font-semibold mb-3 block">Page Permissions</Label>
                  <p className="text-sm text-gray-600 mb-4">Select which pages this user can access</p>
                  <div className="grid grid-cols-2 gap-3">
                    {AVAILABLE_PERMISSIONS.map((permission) => (
                      <div key={permission.id} className="flex items-start space-x-3 p-4 border rounded-lg bg-white">
                        <Checkbox
                          checked={(editingUser.permissions || []).includes(permission.id)}
                          onCheckedChange={() => handlePermissionToggle(permission.id, true)}
                          disabled={editingUser.role === 'admin' || editingUser.role === 'owner'}
                          data-testid={`checkbox-edit-${permission.id}`}
                        />
                        <div className="flex-1">
                          <Label className="font-medium cursor-pointer text-sm">
                            {permission.label}
                          </Label>
                          <p className="text-xs text-gray-500 mt-1">{permission.description}</p>
                        </div>
                      </div>
                    ))}
                  </div>
                  {(editingUser.role === 'admin' || editingUser.role === 'owner') && (
                    <div className="mt-4 p-3 bg-blue-100 border border-blue-200 rounded-md">
                      <p className="text-sm text-blue-800 flex items-center">
                        <Shield className="h-4 w-4 mr-2" />
                        {editingUser.role === 'admin' ? 'Administrators' : 'Owners'} have access to all pages by default
                      </p>
                    </div>
                  )}
                </div>
              </div>

              {/* Password Reset Section */}
              <div className="bg-yellow-50 p-6 rounded-lg">
                <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
                  <Key className="h-5 w-5" />
                  Password Management
                </h3>
                <div className="flex items-center gap-4">
                  <Button 
                    variant="outline" 
                    onClick={() => setResetPasswordUser(editingUser.id)}
                    className="text-orange-600 border-orange-200 hover:bg-orange-50"
                    data-testid="button-reset-password"
                  >
                    <Key className="h-4 w-4 mr-2" />
                    Reset Password
                  </Button>
                  <p className="text-sm text-gray-600">
                    Click to generate a new password for this user account
                  </p>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex justify-end space-x-3 pt-4 border-t">
                <Button variant="outline" onClick={() => setEditingUser(null)}>
                  Cancel Changes
                </Button>
                <Button 
                  onClick={() => updateUserMutation.mutate({ 
                    id: editingUser.id, 
                    data: { 
                      username: editingUser.username,
                      name: editingUser.name,
                      email: editingUser.email,
                      role: editingUser.role, 
                      permissions: editingUser.permissions,
                      is_active: editingUser.is_active 
                    } 
                  })}
                  disabled={updateUserMutation.isPending}
                  className="bg-blue-600 hover:bg-blue-700"
                  data-testid="button-save-edit-user"
                >
                  {updateUserMutation.isPending ? 'Updating...' : 'Save Changes'}
                </Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>

      {/* Password Reset Dialog */}
      <Dialog open={!!resetPasswordUser} onOpenChange={() => setResetPasswordUser(null)}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <Key className="h-5 w-5" />
              Reset User Password
            </DialogTitle>
            <DialogDescription>
              Enter a new password for this user account
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label htmlFor="new-password">New Password</Label>
              <div className="relative">
                <Input
                  id="new-password"
                  type={showPassword ? "text" : "password"}
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  placeholder="Enter new password"
                  data-testid="input-new-password"
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-0 top-0 h-full px-3"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                </Button>
              </div>
            </div>
            <div className="flex justify-end space-x-2">
              <Button 
                variant="outline" 
                onClick={() => {
                  setResetPasswordUser(null);
                  setNewPassword('');
                }}
              >
                Cancel
              </Button>
              <Button 
                onClick={() => resetPasswordUser && resetPasswordMutation.mutate({
                  userId: resetPasswordUser, 
                  password: newPassword
                })}
                disabled={!newPassword || resetPasswordMutation.isPending}
                data-testid="button-confirm-reset"
              >
                {resetPasswordMutation.isPending ? 'Resetting...' : 'Reset Password'}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/admin/activity-log-mana.content:
import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from '@/components/ui/table';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Activity, Search, Filter, RefreshCw, User, FileText, Settings } from 'lucide-react';
import { format } from 'date-fns';

interface ActivityLog {
  id: string;
  user_id?: string;
  action: string;
  entity_type: string;
  entity_id?: string;
  details?: string;
  ip_address?: string;
  user_agent?: string;
  created_at: string;
  user_name?: string; // This might be joined from user table
}

export default function ActivityLogManagement() {
  const [searchTerm, setSearchTerm] = useState('');
  const [entityFilter, setEntityFilter] = useState('all');
  const [actionFilter, setActionFilter] = useState('all');
  const [dateRange, setDateRange] = useState('7'); // days

  const { data: activityLogs = [], isLoading, refetch } = useQuery<ActivityLog[]>({
    queryKey: ['/api/admin/activity-logs', { search: searchTerm, entity: entityFilter, action: actionFilter, days: dateRange }],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (searchTerm) params.append('search', searchTerm);
      if (entityFilter !== 'all') params.append('entity_type', entityFilter);
      if (actionFilter !== 'all') params.append('action', actionFilter);
      if (dateRange !== 'all') params.append('days', dateRange);

      const response = await fetch(`/api/admin/activity-logs?${params.toString()}`);
      if (!response.ok) throw new Error('Failed to fetch activity logs');
      return response.json();
    },
    refetchInterval: 30000 // Refresh every 30 seconds
  });

  const getActionBadge = (action: string) => {
    const actionColors: Record<string, "default" | "secondary" | "destructive"> = {
      'create': 'default',
      'update': 'secondary',
      'delete': 'destructive',
      'login': 'default',
      'logout': 'secondary',
      'view': 'secondary',
      'export': 'default'
    };

    const actionType = action.toLowerCase().split('_')[0];
    return (
      <Badge variant={actionColors[actionType] || "secondary"}>
        {action.replace(/_/g, ' ').toUpperCase()}
      </Badge>
    );
  };

  const getEntityIcon = (entityType: string) => {
    const icons: Record<string, any> = {
      'user': User,
      'lead': FileText,
      'installer': Settings,
      'sample_booklet': FileText,
      'email_template': FileText,
      'smtp_settings': Settings,
      'lead_origin': Settings
    };

    const Icon = icons[entityType] || FileText;
    return <Icon className="h-4 w-4" />;
  };

  const formatUserAgent = (userAgent?: string) => {
    if (!userAgent) return 'Unknown';

    // Simple browser detection
    if (userAgent.includes('Chrome')) return 'Chrome';
    if (userAgent.includes('Firefox')) return 'Firefox';
    if (userAgent.includes('Safari')) return 'Safari';
    if (userAgent.includes('Edge')) return 'Edge';

    return 'Other';
  };

  const clearFilters = () => {
    setSearchTerm('');
    setEntityFilter('all');
    setActionFilter('all');
    setDateRange('7');
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-2">
            <Activity className="h-8 w-8" />
            Activity Log
          </h1>
          <p className="text-gray-600 mt-1">Monitor system usage and user activities</p>
        </div>
        <Button onClick={() => refetch()} size="sm" variant="outline" data-testid="button-refresh-logs">
          <RefreshCw className="h-4 w-4 mr-2" />
          Refresh
        </Button>
      </div>

      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg">Filters</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
            <div>
              <Input
                placeholder="Search activities..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full"
                data-testid="input-search-activities"
              />
            </div>

            <div>
              <Select value={entityFilter} onValueChange={setEntityFilter}>
                <SelectTrigger data-testid="select-entity-filter">
                  <SelectValue placeholder="Entity Type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Entities</SelectItem>
                  <SelectItem value="user">Users</SelectItem>
                  <SelectItem value="lead">Leads</SelectItem>
                  <SelectItem value="installer">Installers</SelectItem>
                  <SelectItem value="sample_booklet">Sample Booklets</SelectItem>
                  <SelectItem value="email_template">Email Templates</SelectItem>
                  <SelectItem value="smtp_settings">SMTP Settings</SelectItem>
                  <SelectItem value="lead_origin">Lead Origins</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Select value={actionFilter} onValueChange={setActionFilter}>
                <SelectTrigger data-testid="select-action-filter">
                  <SelectValue placeholder="Action Type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Actions</SelectItem>
                  <SelectItem value="create">Create</SelectItem>
                  <SelectItem value="update">Update</SelectItem>
                  <SelectItem value="delete">Delete</SelectItem>
                  <SelectItem value="login">Login</SelectItem>
                  <SelectItem value="logout">Logout</SelectItem>
                  <SelectItem value="view">View</SelectItem>
                  <SelectItem value="export">Export</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Select value={dateRange} onValueChange={setDateRange}>
                <SelectTrigger data-testid="select-date-range">
                  <SelectValue placeholder="Date Range" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="1">Last 24 Hours</SelectItem>
                  <SelectItem value="7">Last 7 Days</SelectItem>
                  <SelectItem value="30">Last 30 Days</SelectItem>
                  <SelectItem value="90">Last 90 Days</SelectItem>
                  <SelectItem value="all">All Time</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Button 
                variant="outline" 
                onClick={clearFilters}
                className="w-full"
                data-testid="button-clear-filters"
              >
                <Filter className="h-4 w-4 mr-2" />
                Clear Filters
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Activity Logs Table */}
      <Card>
        <CardHeader>
          <CardTitle>Recent Activities ({activityLogs.length})</CardTitle>
          <CardDescription>
            System and user activity logs - Updates automatically every 30 seconds
          </CardDescription>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="flex justify-center p-8">
              <div className="text-center">
                <div className="animate-spin h-8 w-8 border-b-2 border-primary rounded-full mx-auto mb-2"></div>
                <p>Loading activity logs...</p>
              </div>
            </div>
          ) : activityLogs.length === 0 ? (
            <div className="text-center p-8">
              <Activity className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-500">No activity logs found for the selected filters.</p>
              <Button 
                variant="outline" 
                onClick={clearFilters}
                className="mt-2"
                data-testid="button-clear-filters-empty"
              >
                Clear Filters
              </Button>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Timestamp</TableHead>
                    <TableHead>User</TableHead>
                    <TableHead>Action</TableHead>
                    <TableHead>Entity</TableHead>
                    <TableHead>Details</TableHead>
                    <TableHead>Browser</TableHead>
                    <TableHead>IP Address</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {activityLogs.map((log) => (
                    <TableRow key={log.id} data-testid={`row-activity-${log.id}`}>
                      <TableCell className="font-mono text-sm">
                        {format(new Date(log.created_at), 'MMM dd, HH:mm:ss')}
                      </TableCell>
                      <TableCell>
                        <div className="flex items-center gap-2">
                          <User className="h-4 w-4 text-gray-400" />
                          <span className="font-medium">
                            {log.user_name || log.user_id || 'System'}
                          </span>
                        </div>
                      </TableCell>
                      <TableCell>
                        {getActionBadge(log.action)}
                      </TableCell>
                      <TableCell>
                        <div className="flex items-center gap-2">
                          {getEntityIcon(log.entity_type)}
                          <span className="capitalize">
                            {log.entity_type.replace(/_/g, ' ')}
                          </span>
                        </div>
                      </TableCell>
                      <TableCell className="max-w-xs">
                        <div className="truncate" title={log.details}>
                          {log.details || 'No details'}
                        </div>
                      </TableCell>
                      <TableCell className="text-sm text-gray-600">
                        {formatUserAgent(log.user_agent)}
                      </TableCell>
                      <TableCell className="font-mono text-sm text-gray-600">
                        {log.ip_address || 'N/A'}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Summary Stats */}
      {activityLogs.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardContent className="p-4">
              <div className="text-2xl font-bold">
                {activityLogs.filter(log => log.action.toLowerCase().includes('create')).length}
              </div>
              <p className="text-sm text-gray-600">Items Created</p>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="text-2xl font-bold">
                {activityLogs.filter(log => log.action.toLowerCase().includes('update')).length}
              </div>
              <p className="text-sm text-gray-600">Items Updated</p>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="text-2xl font-bold">
                {activityLogs.filter(log => log.action.toLowerCase().includes('login')).length}
              </div>
              <p className="text-sm text-gray-600">Login Events</p>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="text-2xl font-bold">
                {new Set(activityLogs.map(log => log.user_id).filter(Boolean)).size}
              </div>
              <p className="text-sm text-gray-600">Active Users</p>
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
}

client/src/pages/admin/email-templates-m.content:
import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Switch } from '@/components/ui/switch';
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from '@/components/ui/table';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { Plus, Edit, Trash2, Mail, Eye } from 'lucide-react';

interface EmailTemplate {
  id: string;
  name: string;
  type: string;
  subject: string;
  body: string;
  variables: string[];
  is_active: boolean;
  created_at: string;
  updated_at: string;
}

const EMAIL_TYPES = [
  { value: 'shipping_notification', label: 'Shipping Notification' },
  { value: 'installer_notification', label: 'Installer Notification' },
  { value: 'customer_info', label: 'Customer Installation Info' },
  { value: 'followup_reminder', label: 'Follow-up Reminder' },
  { value: 'quote_sent', label: 'Quote Sent' },
  { value: 'appointment_reminder', label: 'Appointment Reminder' }
];

const AVAILABLE_VARIABLES = [
  '{{customer_name}}', '{{customer_email}}', '{{customer_phone}}',
  '{{order_number}}', '{{tracking_number}}', '{{installation_date}}',
  '{{installer_name}}', '{{installer_phone}}', '{{project_amount}}',
  '{{company_name}}', '{{company_phone}}', '{{company_email}}'
];

export default function EmailTemplatesManagement() {
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [isPreviewModalOpen, setIsPreviewModalOpen] = useState(false);
  const [editingTemplate, setEditingTemplate] = useState<EmailTemplate | null>(null);
  const [previewTemplate, setPreviewTemplate] = useState<EmailTemplate | null>(null);
  const [formData, setFormData] = useState({
    name: '',
    type: '',
    subject: '',
    body: '',
    variables: [] as string[],
    is_active: true
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: templates = [], isLoading } = useQuery<EmailTemplate[]>({
    queryKey: ['/api/admin/email-templates'],
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest('/api/admin/email-templates', {
        method: 'POST',
        body: JSON.stringify(data)
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/email-templates'] });
      setIsAddModalOpen(false);
      resetForm();
      toast({ title: "Success", description: "Email template created successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create email template", variant: "destructive" });
    }
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, ...data }: any) => {
      return apiRequest(`/api/admin/email-templates/${id}`, {
        method: 'PUT',
        body: JSON.stringify(data)
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/email-templates'] });
      setIsEditModalOpen(false);
      setEditingTemplate(null);
      resetForm();
      toast({ title: "Success", description: "Email template updated successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to update email template", variant: "destructive" });
    }
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest(`/api/admin/email-templates/${id}`, { method: 'DELETE' });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/email-templates'] });
      toast({ title: "Success", description: "Email template deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete email template", variant: "destructive" });
    }
  });

  const resetForm = () => {
    setFormData({
      name: '',
      type: '',
      subject: '',
      body: '',
      variables: [],
      is_active: true
    });
  };

  const handleAdd = () => {
    resetForm();
    setIsAddModalOpen(true);
  };

  const handleEdit = (template: EmailTemplate) => {
    setEditingTemplate(template);
    setFormData({
      name: template.name,
      type: template.type,
      subject: template.subject,
      body: template.body,
      variables: template.variables || [],
      is_active: template.is_active
    });
    setIsEditModalOpen(true);
  };

  const handlePreview = (template: EmailTemplate) => {
    setPreviewTemplate(template);
    setIsPreviewModalOpen(true);
  };

  const handleDelete = (template: EmailTemplate) => {
    if (confirm(`Are you sure you want to delete template "${template.name}"?`)) {
      deleteMutation.mutate(template.id);
    }
  };

  const handleSubmit = (isEdit: boolean) => {
    if (!formData.name || !formData.type || !formData.subject || !formData.body) {
      toast({ title: "Error", description: "All fields are required", variant: "destructive" });
      return;
    }

    // Extract variables from body text
    const extractedVariables = AVAILABLE_VARIABLES.filter(variable => 
      formData.body.includes(variable) || formData.subject.includes(variable)
    );

    const templateData = {
      ...formData,
      variables: extractedVariables
    };

    if (isEdit && editingTemplate) {
      updateMutation.mutate({ id: editingTemplate.id, ...templateData });
    } else {
      createMutation.mutate(templateData);
    }
  };

  const toggleStatus = (template: EmailTemplate) => {
    updateMutation.mutate({ 
      id: template.id, 
      is_active: !template.is_active 
    });
  };

  const insertVariable = (variable: string) => {
    const textarea = document.getElementById('body') as HTMLTextAreaElement;
    if (textarea) {
      const start = textarea.selectionStart;
      const end = textarea.selectionEnd;
      const text = formData.body;
      const newText = text.substring(0, start) + variable + text.substring(end);
      setFormData(prev => ({ ...prev, body: newText }));

      // Restore cursor position
      setTimeout(() => {
        textarea.selectionStart = textarea.selectionEnd = start + variable.length;
        textarea.focus();
      }, 0);
    }
  };

  const getTypeLabel = (type: string) => {
    return EMAIL_TYPES.find(t => t.value === type)?.label || type;
  };

  const FormModal = ({ isOpen, setIsOpen, title, onSubmit, isEdit }: any) => (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>{title}</DialogTitle>
        </DialogHeader>
        <div className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="name">Template Name *</Label>
              <Input
                id="name"
                value={formData.name}
                onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                data-testid="input-template-name"
              />
            </div>
            <div>
              <Label htmlFor="type">Template Type *</Label>
              <Select 
                value={formData.type} 
                onValueChange={(value) => setFormData(prev => ({ ...prev, type: value }))}
              >
                <SelectTrigger data-testid="select-template-type">
                  <SelectValue placeholder="Select template type" />
                </SelectTrigger>
                <SelectContent>
                  {EMAIL_TYPES.map(type => (
                    <SelectItem key={type.value} value={type.value}>
                      {type.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          <div>
            <Label htmlFor="subject">Email Subject *</Label>
            <Input
              id="subject"
              value={formData.subject}
              onChange={(e) => setFormData(prev => ({ ...prev, subject: e.target.value }))}
              data-testid="input-template-subject"
            />
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-4">
            <div className="lg:col-span-2">
              <Label htmlFor="body">Email Body *</Label>
              <Textarea
                id="body"
                rows={12}
                value={formData.body}
                onChange={(e) => setFormData(prev => ({ ...prev, body: e.target.value }))}
                data-testid="textarea-template-body"
                className="font-mono"
              />
            </div>
            <div>
              <Label>Available Variables</Label>
              <div className="space-y-2 max-h-60 overflow-y-auto border rounded p-2">
                {AVAILABLE_VARIABLES.map(variable => (
                  <Button
                    key={variable}
                    variant="outline"
                    size="sm"
                    className="w-full justify-start text-xs"
                    onClick={() => insertVariable(variable)}
                    data-testid={`button-variable-${variable.replace(/[{}]/g, '')}`}
                  >
                    {variable}
                  </Button>
                ))}
              </div>
              <p className="text-xs text-gray-500 mt-2">Click to insert into email body</p>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <Switch
              id="is_active"
              checked={formData.is_active}
              onCheckedChange={(checked) => setFormData(prev => ({ ...prev, is_active: checked }))}
              data-testid="switch-template-active"
            />
            <Label htmlFor="is_active">Template Active</Label>
          </div>

          <div className="flex justify-end gap-2 pt-4">
            <Button 
              variant="outline" 
              onClick={() => setIsOpen(false)}
              data-testid="button-cancel"
            >
              Cancel
            </Button>
            <Button 
              onClick={() => onSubmit(isEdit)}
              disabled={createMutation.isPending || updateMutation.isPending}
              data-testid="button-save-template"
            >
              {createMutation.isPending || updateMutation.isPending ? 'Saving...' : 'Save Template'}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );

  const PreviewModal = () => (
    <Dialog open={isPreviewModalOpen} onOpenChange={setIsPreviewModalOpen}>
      <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Preview: {previewTemplate?.name}</DialogTitle>
        </DialogHeader>
        {previewTemplate && (
          <div className="space-y-4">
            <div>
              <Label>Subject:</Label>
              <div className="border rounded p-3 bg-gray-50">
                {previewTemplate.subject}
              </div>
            </div>
            <div>
              <Label>Email Body:</Label>
              <div className="border rounded p-4 bg-white whitespace-pre-wrap">
                {previewTemplate.body}
              </div>
            </div>
            {previewTemplate.variables.length > 0 && (
              <div>
                <Label>Variables Used:</Label>
                <div className="flex flex-wrap gap-2 mt-2">
                  {previewTemplate.variables.map(variable => (
                    <Badge key={variable} variant="outline">
                      {variable}
                    </Badge>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}
      </DialogContent>
    </Dialog>
  );

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-2">
            <Mail className="h-8 w-8" />
            Email Templates Management
          </h1>
          <p className="text-gray-600 mt-1">Configure email templates for notifications and communication</p>
        </div>
        <Button onClick={handleAdd} data-testid="button-add-template">
          <Plus className="h-4 w-4 mr-2" />
          Add Template
        </Button>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Email Templates ({templates.length})</CardTitle>
          <CardDescription>
            Manage templates for shipping notifications, installer communications, and customer updates
          </CardDescription>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="flex justify-center p-8">
              <div className="text-center">
                <div className="animate-spin h-8 w-8 border-b-2 border-primary rounded-full mx-auto mb-2"></div>
                <p>Loading email templates...</p>
              </div>
            </div>
          ) : templates.length === 0 ? (
            <div className="text-center p-8">
              <Mail className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-500">No email templates found. Create your first template to start sending automated emails.</p>
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Template Name</TableHead>
                  <TableHead>Type</TableHead>
                  <TableHead>Subject Preview</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {templates.map((template) => (
                  <TableRow key={template.id} data-testid={`row-template-${template.id}`}>
                    <TableCell className="font-medium">{template.name}</TableCell>
                    <TableCell>{getTypeLabel(template.type)}</TableCell>
                    <TableCell className="max-w-xs truncate" title={template.subject}>
                      {template.subject}
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <Switch
                          checked={template.is_active}
                          onCheckedChange={() => toggleStatus(template)}
                          disabled={updateMutation.isPending}
                          data-testid={`switch-template-status-${template.id}`}
                        />
                        <Badge variant={template.is_active ? "default" : "secondary"}>
                          {template.is_active ? "Active" : "Inactive"}
                        </Badge>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handlePreview(template)}
                          data-testid={`button-preview-template-${template.id}`}
                        >
                          <Eye className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleEdit(template)}
                          data-testid={`button-edit-template-${template.id}`}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleDelete(template)}
                          data-testid={`button-delete-template-${template.id}`}
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>

      <FormModal
        isOpen={isAddModalOpen}
        setIsOpen={setIsAddModalOpen}
        title="Add New Email Template"
        onSubmit={(isEdit: boolean) => handleSubmit(isEdit)}
        isEdit={false}
      />

      <FormModal
        isOpen={isEditModalOpen}
        setIsOpen={setIsEditModalOpen}
        title="Edit Email Template"
        onSubmit={(isEdit: boolean) => handleSubmit(isEdit)}
        isEdit={true}
      />

      <PreviewModal />
    </div>
  );
}

client/src/pages/admin/installers-manage.content:
import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from '@/components/ui/table';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { Plus, Edit, Trash2, UserCog } from 'lucide-react';

interface Installer {
  id: string;
  name: string;
  phone?: string;
  email?: string;
  status: string;
  hire_date?: string;
  hourly_rate?: number;
  specialty?: string;
  notes?: string;
  created_at: string;
  updated_at: string;
}

export default function InstallersManagement() {
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [editingInstaller, setEditingInstaller] = useState<Installer | null>(null);
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
    status: 'active',
    hire_date: '',
    hourly_rate: '',
    specialty: '',
    notes: ''
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: installers = [], isLoading } = useQuery<Installer[]>({
    queryKey: ['/api/admin/installers'],
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      const payload = {
        ...data,
        hourly_rate: data.hourly_rate ? parseFloat(data.hourly_rate) : null
      };
      return apiRequest('POST', '/api/admin/installers', payload);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/installers'] });
      setIsAddModalOpen(false);
      resetForm();
      toast({ title: "Success", description: "Installer created successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create installer", variant: "destructive" });
    }
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, ...data }: any) => {
      const payload = {
        ...data,
        hourly_rate: data.hourly_rate ? parseFloat(data.hourly_rate) : null
      };
      return apiRequest('PUT', `/api/admin/installers/${id}`, payload);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/installers'] });
      setIsEditModalOpen(false);
      setEditingInstaller(null);
      resetForm();
      toast({ title: "Success", description: "Installer updated successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to update installer", variant: "destructive" });
    }
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest('DELETE', `/api/admin/installers/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/installers'] });
      toast({ title: "Success", description: "Installer deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete installer", variant: "destructive" });
    }
  });

  const resetForm = () => {
    setFormData({
      name: '',
      phone: '',
      email: '',
      status: 'active',
      hire_date: '',
      hourly_rate: '',
      specialty: '',
      notes: ''
    });
  };

  const handleAdd = () => {
    resetForm();
    setIsAddModalOpen(true);
  };

  const handleEdit = (installer: Installer) => {
    setEditingInstaller(installer);
    setFormData({
      name: installer.name,
      phone: installer.phone || '',
      email: installer.email || '',
      status: installer.status,
      hire_date: installer.hire_date || '',
      hourly_rate: installer.hourly_rate?.toString() || '',
      specialty: installer.specialty || '',
      notes: installer.notes || ''
    });
    setIsEditModalOpen(true);
  };

  const handleDelete = (installer: Installer) => {
    if (confirm(`Are you sure you want to delete installer "${installer.name}"?`)) {
      deleteMutation.mutate(installer.id);
    }
  };

  const handleSubmit = (isEdit: boolean) => {
    if (!formData.name) {
      toast({ title: "Error", description: "Name is required", variant: "destructive" });
      return;
    }

    if (isEdit && editingInstaller) {
      updateMutation.mutate({ id: editingInstaller.id, ...formData });
    } else {
      createMutation.mutate(formData);
    }
  };

  const getStatusBadge = (status: string) => {
    const variants: Record<string, "default" | "secondary" | "destructive"> = {
      active: "default",
      inactive: "secondary",
      on_leave: "secondary",
      terminated: "destructive"
    };

    return (
      <Badge variant={variants[status] || "default"}>
        {status.replace('_', ' ').toUpperCase()}
      </Badge>
    );
  };

  const FormModal = ({ isOpen, setIsOpen, title, onSubmit, isEdit }: any) => (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle>{title}</DialogTitle>
        </DialogHeader>
        <div className="space-y-4">
          <div>
            <Label htmlFor="name">Name *</Label>
            <Input
              id="name"
              value={formData.name}
              onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
              data-testid="input-installer-name"
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="phone">Phone</Label>
              <Input
                id="phone"
                value={formData.phone}
                onChange={(e) => setFormData(prev => ({ ...prev, phone: e.target.value }))}
                data-testid="input-installer-phone"
              />
            </div>
            <div>
              <Label htmlFor="email">Email</Label>
              <Input
                id="email"
                type="email"
                value={formData.email}
                onChange={(e) => setFormData(prev => ({ ...prev, email: e.target.value }))}
                data-testid="input-installer-email"
              />
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="status">Status</Label>
              <Select 
                value={formData.status} 
                onValueChange={(value) => setFormData(prev => ({ ...prev, status: value }))}
              >
                <SelectTrigger data-testid="select-installer-status">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="active">Active</SelectItem>
                  <SelectItem value="inactive">Inactive</SelectItem>
                  <SelectItem value="on_leave">On Leave</SelectItem>
                  <SelectItem value="terminated">Terminated</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="hire_date">Hire Date</Label>
              <Input
                id="hire_date"
                type="date"
                value={formData.hire_date}
                onChange={(e) => setFormData(prev => ({ ...prev, hire_date: e.target.value }))}
                data-testid="input-installer-hire-date"
              />
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="hourly_rate">Hourly Rate</Label>
              <Input
                id="hourly_rate"
                type="number"
                step="0.01"
                placeholder="25.00"
                value={formData.hourly_rate}
                onChange={(e) => setFormData(prev => ({ ...prev, hourly_rate: e.target.value }))}
                data-testid="input-installer-hourly-rate"
              />
            </div>
            <div>
              <Label htmlFor="specialty">Specialty</Label>
              <Input
                id="specialty"
                value={formData.specialty}
                onChange={(e) => setFormData(prev => ({ ...prev, specialty: e.target.value }))}
                data-testid="input-installer-specialty"
              />
            </div>
          </div>

          <div>
            <Label htmlFor="notes">Notes</Label>
            <Textarea
              id="notes"
              rows={3}
              value={formData.notes}
              onChange={(e) => setFormData(prev => ({ ...prev, notes: e.target.value }))}
              data-testid="textarea-installer-notes"
            />
          </div>

          <div className="flex justify-end gap-2 pt-4">
            <Button 
              variant="outline" 
              onClick={() => setIsOpen(false)}
              data-testid="button-cancel"
            >
              Cancel
            </Button>
            <Button 
              onClick={() => onSubmit(isEdit)}
              disabled={createMutation.isPending || updateMutation.isPending}
              data-testid="button-save-installer"
            >
              {createMutation.isPending || updateMutation.isPending ? 'Saving...' : 'Save'}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-2">
            <UserCog className="h-8 w-8" />
            Installer Management
          </h1>
          <p className="text-gray-600 mt-1">Manage installer profiles and information</p>
        </div>
        <Button onClick={handleAdd} data-testid="button-add-installer">
          <Plus className="h-4 w-4 mr-2" />
          Add Installer
        </Button>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Installers ({installers.length})</CardTitle>
          <CardDescription>
            Manage your installation team members
          </CardDescription>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="flex justify-center p-8">
              <div className="text-center">
                <div className="animate-spin h-8 w-8 border-b-2 border-primary rounded-full mx-auto mb-2"></div>
                <p>Loading installers...</p>
              </div>
            </div>
          ) : installers.length === 0 ? (
            <div className="text-center p-8">
              <UserCog className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-500">No installers found. Add your first installer to get started.</p>
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Name</TableHead>
                  <TableHead>Contact</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Hourly Rate</TableHead>
                  <TableHead>Specialty</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {installers.map((installer) => (
                  <TableRow key={installer.id} data-testid={`row-installer-${installer.id}`}>
                    <TableCell className="font-medium">{installer.name}</TableCell>
                    <TableCell>
                      <div className="text-sm">
                        {installer.email && <div>{installer.email}</div>}
                        {installer.phone && <div className="text-gray-500">{installer.phone}</div>}
                      </div>
                    </TableCell>
                    <TableCell>{getStatusBadge(installer.status)}</TableCell>
                    <TableCell>
                      {installer.hourly_rate ? `$${installer.hourly_rate}/hr` : 'Not set'}
                    </TableCell>
                    <TableCell>{installer.specialty || 'General'}</TableCell>
                    <TableCell>
                      <div className="flex gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleEdit(installer)}
                          data-testid={`button-edit-installer-${installer.id}`}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleDelete(installer)}
                          data-testid={`button-delete-installer-${installer.id}`}
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>

      <FormModal
        isOpen={isAddModalOpen}
        setIsOpen={setIsAddModalOpen}
        title="Add New Installer"
        onSubmit={(isEdit: boolean) => handleSubmit(isEdit)}
        isEdit={false}
      />

      <FormModal
        isOpen={isEditModalOpen}
        setIsOpen={setIsEditModalOpen}
        title="Edit Installer"
        onSubmit={(isEdit: boolean) => handleSubmit(isEdit)}
        isEdit={true}
      />
    </div>
  );
}

client/src/pages/admin/lead-origins-mana.content:
import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Switch } from '@/components/ui/switch';
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from '@/components/ui/table';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { Plus, Edit, Trash2, Globe } from 'lucide-react';

interface LeadOrigin {
  id: string;
  name: string;
  display_name: string;
  is_active: boolean;
  sort_order: number;
  created_at: string;
  updated_at: string;
}

export default function LeadOriginsManagement() {
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [editingOrigin, setEditingOrigin] = useState<LeadOrigin | null>(null);
  const [formData, setFormData] = useState({
    name: '',
    display_name: '',
    is_active: true,
    sort_order: 0
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: origins = [], isLoading } = useQuery<LeadOrigin[]>({
    queryKey: ['/api/admin/lead-origins'],
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest('/api/admin/lead-origins', {
        method: 'POST',
        body: JSON.stringify(data)
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/lead-origins'] });
      setIsAddModalOpen(false);
      resetForm();
      toast({ title: "Success", description: "Lead origin created successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create lead origin", variant: "destructive" });
    }
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, ...data }: any) => {
      return apiRequest(`/api/admin/lead-origins/${id}`, {
        method: 'PUT',
        body: JSON.stringify(data)
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/lead-origins'] });
      setIsEditModalOpen(false);
      setEditingOrigin(null);
      resetForm();
      toast({ title: "Success", description: "Lead origin updated successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to update lead origin", variant: "destructive" });
    }
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest(`/api/admin/lead-origins/${id}`, { method: 'DELETE' });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/lead-origins'] });
      toast({ title: "Success", description: "Lead origin deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete lead origin", variant: "destructive" });
    }
  });

  const resetForm = () => {
    setFormData({
      name: '',
      display_name: '',
      is_active: true,
      sort_order: 0
    });
  };

  const handleAdd = () => {
    resetForm();
    setIsAddModalOpen(true);
  };

  const handleEdit = (origin: LeadOrigin) => {
    setEditingOrigin(origin);
    setFormData({
      name: origin.name,
      display_name: origin.display_name,
      is_active: origin.is_active,
      sort_order: origin.sort_order
    });
    setIsEditModalOpen(true);
  };

  const handleDelete = (origin: LeadOrigin) => {
    if (confirm(`Are you sure you want to delete lead origin "${origin.display_name}"?`)) {
      deleteMutation.mutate(origin.id);
    }
  };

  const handleSubmit = (isEdit: boolean) => {
    if (!formData.name || !formData.display_name) {
      toast({ title: "Error", description: "Name and display name are required", variant: "destructive" });
      return;
    }

    if (isEdit && editingOrigin) {
      updateMutation.mutate({ id: editingOrigin.id, ...formData });
    } else {
      createMutation.mutate(formData);
    }
  };

  const toggleStatus = (origin: LeadOrigin) => {
    updateMutation.mutate({ 
      id: origin.id, 
      is_active: !origin.is_active 
    });
  };

  const FormModal = ({ isOpen, setIsOpen, title, onSubmit, isEdit }: any) => (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle>{title}</DialogTitle>
        </DialogHeader>
        <div className="space-y-4">
          <div>
            <Label htmlFor="name">Internal Name *</Label>
            <Input
              id="name"
              placeholder="e.g., facebook_ads"
              value={formData.name}
              onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value.toLowerCase().replace(/\s+/g, '_') }))}
              data-testid="input-origin-name"
            />
            <p className="text-xs text-gray-500 mt-1">Used internally, no spaces or special characters</p>
          </div>

          <div>
            <Label htmlFor="display_name">Display Name *</Label>
            <Input
              id="display_name"
              placeholder="e.g., Facebook Ads"
              value={formData.display_name}
              onChange={(e) => setFormData(prev => ({ ...prev, display_name: e.target.value }))}
              data-testid="input-origin-display-name"
            />
            <p className="text-xs text-gray-500 mt-1">Name shown to users in dropdowns</p>
          </div>

          <div>
            <Label htmlFor="sort_order">Sort Order</Label>
            <Input
              id="sort_order"
              type="number"
              value={formData.sort_order}
              onChange={(e) => setFormData(prev => ({ ...prev, sort_order: parseInt(e.target.value) || 0 }))}
              data-testid="input-origin-sort-order"
            />
            <p className="text-xs text-gray-500 mt-1">Lower numbers appear first in lists</p>
          </div>

          <div className="flex items-center space-x-2">
            <Switch
              id="is_active"
              checked={formData.is_active}
              onCheckedChange={(checked) => setFormData(prev => ({ ...prev, is_active: checked }))}
              data-testid="switch-origin-active"
            />
            <Label htmlFor="is_active">Active</Label>
          </div>

          <div className="flex justify-end gap-2 pt-4">
            <Button 
              variant="outline" 
              onClick={() => setIsOpen(false)}
              data-testid="button-cancel"
            >
              Cancel
            </Button>
            <Button 
              onClick={() => onSubmit(isEdit)}
              disabled={createMutation.isPending || updateMutation.isPending}
              data-testid="button-save-origin"
            >
              {createMutation.isPending || updateMutation.isPending ? 'Saving...' : 'Save'}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-2">
            <Globe className="h-8 w-8" />
            Lead Origins Management
          </h1>
          <p className="text-gray-600 mt-1">Configure lead sources and marketing channels</p>
        </div>
        <Button onClick={handleAdd} data-testid="button-add-origin">
          <Plus className="h-4 w-4 mr-2" />
          Add Lead Origin
        </Button>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Lead Origins ({origins.length})</CardTitle>
          <CardDescription>
            Manage where your leads come from - social media, referrals, advertising channels, etc.
          </CardDescription>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="flex justify-center p-8">
              <div className="text-center">
                <div className="animate-spin h-8 w-8 border-b-2 border-primary rounded-full mx-auto mb-2"></div>
                <p>Loading lead origins...</p>
              </div>
            </div>
          ) : origins.length === 0 ? (
            <div className="text-center p-8">
              <Globe className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-500">No custom lead origins found. Add your first lead origin to track where leads come from.</p>
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Display Name</TableHead>
                  <TableHead>Internal Name</TableHead>
                  <TableHead>Sort Order</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {origins
                  .sort((a, b) => a.sort_order - b.sort_order)
                  .map((origin) => (
                    <TableRow key={origin.id} data-testid={`row-origin-${origin.id}`}>
                      <TableCell className="font-medium">{origin.display_name}</TableCell>
                      <TableCell className="font-mono text-sm text-gray-600">{origin.name}</TableCell>
                      <TableCell>{origin.sort_order}</TableCell>
                      <TableCell>
                        <div className="flex items-center gap-2">
                          <Switch
                            checked={origin.is_active}
                            onCheckedChange={() => toggleStatus(origin)}
                            disabled={updateMutation.isPending}
                            data-testid={`switch-origin-status-${origin.id}`}
                          />
                          <Badge variant={origin.is_active ? "default" : "secondary"}>
                            {origin.is_active ? "Active" : "Inactive"}
                          </Badge>
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="flex gap-2">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleEdit(origin)}
                            data-testid={`button-edit-origin-${origin.id}`}
                          >
                            <Edit className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleDelete(origin)}
                            data-testid={`button-delete-origin-${origin.id}`}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>

      <FormModal
        isOpen={isAddModalOpen}
        setIsOpen={setIsAddModalOpen}
        title="Add New Lead Origin"
        onSubmit={(isEdit: boolean) => handleSubmit(isEdit)}
        isEdit={false}
      />

      <FormModal
        isOpen={isEditModalOpen}
        setIsOpen={setIsEditModalOpen}
        title="Edit Lead Origin"
        onSubmit={(isEdit: boolean) => handleSubmit(isEdit)}
        isEdit={true}
      />
    </div>
  );
}

client/src/pages/admin/smtp-settings-man.content:
import { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { Settings, Mail, TestTube, Save, Eye, EyeOff } from 'lucide-react';

interface SMTPSettings {
  id?: string;
  host: string;
  port: number;
  username: string;
  password: string;
  from_email: string;
  from_name: string;
  use_tls: boolean;
  is_active: boolean;
  test_email?: string;
  created_at?: string;
  updated_at?: string;
}

export default function SMTPSettingsManagement() {
  const [formData, setFormData] = useState<SMTPSettings>({
    host: '',
    port: 587,
    username: '',
    password: '',
    from_email: '',
    from_name: 'WMK Kitchen Solutions',
    use_tls: true,
    is_active: false,
    test_email: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [hasChanges, setHasChanges] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: settings, isLoading } = useQuery<SMTPSettings[]>({
    queryKey: ['/api/admin/smtp-settings'],
  });

  const saveMutation = useMutation({
    mutationFn: async (data: SMTPSettings) => {
      const method = settings && settings.length > 0 ? 'PUT' : 'POST';
      const url = settings && settings.length > 0 
        ? `/api/admin/smtp-settings/${settings[0].id}` 
        : '/api/admin/smtp-settings';

      return apiRequest(url, {
        method,
        body: JSON.stringify(data)
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/smtp-settings'] });
      setHasChanges(false);
      toast({ title: "Success", description: "SMTP settings saved successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to save SMTP settings", variant: "destructive" });
    }
  });

  const testMutation = useMutation({
    mutationFn: async (testEmail: string) => {
      return apiRequest('/api/admin/smtp-settings/test', {
        method: 'POST',
        body: JSON.stringify({ 
          ...formData,
          test_email: testEmail 
        })
      });
    },
    onSuccess: () => {
      toast({ title: "Success", description: "Test email sent successfully" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Test Failed", 
        description: error.message || "Failed to send test email", 
        variant: "destructive" 
      });
    }
  });

  // Load existing settings into form
  useEffect(() => {
    if (settings && settings.length > 0) {
      const currentSettings = settings[0];
      setFormData({
        ...currentSettings,
        test_email: currentSettings.test_email || ''
      });
    }
  }, [settings]);

  const handleInputChange = (field: keyof SMTPSettings, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    setHasChanges(true);
  };

  const handleSave = () => {
    if (!formData.host || !formData.username || !formData.password || !formData.from_email) {
      toast({ title: "Error", description: "Please fill in all required fields", variant: "destructive" });
      return;
    }

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.from_email)) {
      toast({ title: "Error", description: "Please enter a valid email address", variant: "destructive" });
      return;
    }

    saveMutation.mutate(formData);
  };

  const handleTest = () => {
    if (!formData.test_email) {
      toast({ title: "Error", description: "Please enter a test email address", variant: "destructive" });
      return;
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.test_email)) {
      toast({ title: "Error", description: "Please enter a valid test email address", variant: "destructive" });
      return;
    }

    testMutation.mutate(formData.test_email);
  };

  const presetConfigurations = [
    {
      name: 'Gmail',
      host: 'smtp.gmail.com',
      port: 587,
      use_tls: true,
      note: 'Use App Password instead of regular password'
    },
    {
      name: 'Outlook/Hotmail',
      host: 'smtp-mail.outlook.com',
      port: 587,
      use_tls: true,
      note: 'Microsoft 365 and Outlook.com'
    },
    {
      name: 'Yahoo',
      host: 'smtp.mail.yahoo.com',
      port: 587,
      use_tls: true,
      note: 'Enable "Less secure app access"'
    }
  ];

  const applyPreset = (preset: typeof presetConfigurations[0]) => {
    setFormData(prev => ({
      ...prev,
      host: preset.host,
      port: preset.port,
      use_tls: preset.use_tls
    }));
    setHasChanges(true);
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-2">
            <Settings className="h-8 w-8" />
            SMTP Configuration
          </h1>
          <p className="text-gray-600 mt-1">Configure email server settings for automated notifications</p>
        </div>
        <div className="flex items-center gap-2">
          {settings && settings.length > 0 && settings[0].is_active && (
            <Badge className="bg-green-100 text-green-800">
              <Mail className="h-3 w-3 mr-1" />
              Active
            </Badge>
          )}
          {hasChanges && (
            <Badge variant="outline" className="text-orange-600">
              Unsaved Changes
            </Badge>
          )}
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Main Settings Form */}
        <div className="lg:col-span-2">
          <Card>
            <CardHeader>
              <CardTitle>SMTP Server Configuration</CardTitle>
              <CardDescription>
                Configure your email server settings to send automated notifications
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {isLoading ? (
                <div className="flex justify-center p-8">
                  <div className="text-center">
                    <div className="animate-spin h-8 w-8 border-b-2 border-primary rounded-full mx-auto mb-2"></div>
                    <p>Loading SMTP settings...</p>
                  </div>
                </div>
              ) : (
                <>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="host">SMTP Host *</Label>
                      <Input
                        id="host"
                        value={formData.host}
                        onChange={(e) => handleInputChange('host', e.target.value)}
                        placeholder="smtp.gmail.com"
                        data-testid="input-smtp-host"
                      />
                    </div>
                    <div>
                      <Label htmlFor="port">Port *</Label>
                      <Input
                        id="port"
                        type="number"
                        value={formData.port}
                        onChange={(e) => handleInputChange('port', parseInt(e.target.value) || 587)}
                        data-testid="input-smtp-port"
                      />
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="username">Username *</Label>
                    <Input
                      id="username"
                      value={formData.username}
                      onChange={(e) => handleInputChange('username', e.target.value)}
                      placeholder="your-email@example.com"
                      data-testid="input-smtp-username"
                    />
                  </div>

                  <div>
                    <Label htmlFor="password">Password *</Label>
                    <div className="flex gap-2">
                      <Input
                        id="password"
                        type={showPassword ? "text" : "password"}
                        value={formData.password}
                        onChange={(e) => handleInputChange('password', e.target.value)}
                        placeholder="your-app-password"
                        data-testid="input-smtp-password"
                      />
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setShowPassword(!showPassword)}
                        data-testid="button-toggle-password"
                      >
                        {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                      </Button>
                    </div>
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="from_email">From Email *</Label>
                      <Input
                        id="from_email"
                        type="email"
                        value={formData.from_email}
                        onChange={(e) => handleInputChange('from_email', e.target.value)}
                        placeholder="noreply@yourcompany.com"
                        data-testid="input-smtp-from-email"
                      />
                    </div>
                    <div>
                      <Label htmlFor="from_name">From Name *</Label>
                      <Input
                        id="from_name"
                        value={formData.from_name}
                        onChange={(e) => handleInputChange('from_name', e.target.value)}
                        placeholder="WMK Kitchen Solutions"
                        data-testid="input-smtp-from-name"
                      />
                    </div>
                  </div>

                  <div className="flex items-center space-x-2">
                    <Switch
                      id="use_tls"
                      checked={formData.use_tls}
                      onCheckedChange={(checked) => handleInputChange('use_tls', checked)}
                      data-testid="switch-smtp-tls"
                    />
                    <Label htmlFor="use_tls">Use TLS/STARTTLS</Label>
                  </div>

                  <div className="flex items-center space-x-2">
                    <Switch
                      id="is_active"
                      checked={formData.is_active}
                      onCheckedChange={(checked) => handleInputChange('is_active', checked)}
                      data-testid="switch-smtp-active"
                    />
                    <Label htmlFor="is_active">Enable SMTP (Activate email sending)</Label>
                  </div>

                  <div className="flex gap-2 pt-4">
                    <Button 
                      onClick={handleSave}
                      disabled={saveMutation.isPending || !hasChanges}
                      data-testid="button-save-smtp"
                    >
                      <Save className="h-4 w-4 mr-2" />
                      {saveMutation.isPending ? 'Saving...' : 'Save Settings'}
                    </Button>
                  </div>
                </>
              )}
            </CardContent>
          </Card>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Test Email */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Test Configuration</CardTitle>
              <CardDescription>
                Send a test email to verify your settings
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-3">
              <div>
                <Label htmlFor="test_email">Test Email Address</Label>
                <Input
                  id="test_email"
                  type="email"
                  value={formData.test_email}
                  onChange={(e) => handleInputChange('test_email', e.target.value)}
                  placeholder="test@example.com"
                  data-testid="input-test-email"
                />
              </div>
              <Button 
                onClick={handleTest}
                disabled={testMutation.isPending || !formData.test_email}
                className="w-full"
                variant="outline"
                data-testid="button-test-smtp"
              >
                <TestTube className="h-4 w-4 mr-2" />
                {testMutation.isPending ? 'Sending...' : 'Send Test Email'}
              </Button>
            </CardContent>
          </Card>

          {/* Preset Configurations */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Quick Setup</CardTitle>
              <CardDescription>
                Common email provider configurations
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-3">
              {presetConfigurations.map((preset) => (
                <div key={preset.name} className="border rounded p-3">
                  <div className="flex items-center justify-between mb-2">
                    <span className="font-medium">{preset.name}</span>
                    <Button 
                      size="sm" 
                      variant="outline"
                      onClick={() => applyPreset(preset)}
                      data-testid={`button-preset-${preset.name.toLowerCase()}`}
                    >
                      Use
                    </Button>
                  </div>
                  <p className="text-xs text-gray-500">{preset.note}</p>
                  <div className="text-xs text-gray-400 mt-1">
                    {preset.host}:{preset.port}
                  </div>
                </div>
              ))}
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/components/calendar/BusinessC.content:
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import { useQuery } from '@tanstack/react-query';
import { CalendarEvent, Lead } from '@shared/schema';

interface CalendarEventDisplay {
  id: string;
  title: string;
  start: string;
  end?: string;
  allDay?: boolean;
  color: string;
  extendedProps: {
    type: string;
    description?: string | null;
    location?: string | null;
    assignedTo?: string | null;
  };
}

export function BusinessCalendar() {
  // Fetch installations (already in database)
  const { data: installations = [], isLoading: installationsLoading } = useQuery<Lead[]>({
    queryKey: ['/api/installations'],
  });

  // Fetch calendar events - temporarily disabled to fix issues
  const events: CalendarEvent[] = [];
  const eventsLoading = false;

  if (installationsLoading || eventsLoading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '400px' }}>
        <div className="text-center">
          <i className="fas fa-spinner fa-spin fa-2x text-primary mb-2"></i>
          <p className="text-muted">Loading calendar...</p>
        </div>
      </div>
    );
  }

  // Convert installations to calendar events
  const installationEvents: CalendarEventDisplay[] = installations
    .filter(lead => lead.installation_date && lead.remarks === 'sold')
    .map(lead => ({
      id: `installation-${lead.id}`,
      title: `Installation: ${lead.name}`,
      start: new Date(lead.installation_date!).toISOString(),
      allDay: false,
      color: '#10B981', // Green for installations
      extendedProps: {
        type: 'installation',
        description: `Project: ${lead.project_amount ? `$${lead.project_amount}` : 'N/A'}`,
        location: 'Customer Location',
        assignedTo: Array.isArray(lead.assigned_installer) && lead.assigned_installer.length > 0
          ? lead.assigned_installer.join(', ') 
          : 'Not assigned',
      },
    }));

  // Convert other events to calendar format
  const otherEvents: CalendarEventDisplay[] = events.map(event => {
    let color = '#6B7280'; // Default gray

    switch (event.type) {
      case 'leave':
        color = '#EF4444'; // Red
        break;
      case 'trade-show':
        color = '#8B5CF6'; // Purple
        break;
      case 'showroom-visit':
        color = '#F59E0B'; // Amber
        break;
      case 'holiday':
        color = '#EC4899'; // Pink
        break;
    }

    return {
      id: event.id,
      title: event.title,
      start: event.start_date.toISOString(),
      end: event.end_date?.toISOString(),
      allDay: event.all_day,
      color,
      extendedProps: {
        type: event.type,
        description: event.description,
        location: event.location,
        assignedTo: event.assigned_to,
      },
    };
  });

  const allEvents = [...installationEvents, ...otherEvents];

  const handleEventClick = (info: any) => {
    const { event } = info;
    const { extendedProps } = event;

    let alertMessage = `${event.title}\n`;
    alertMessage += `Type: ${extendedProps.type.replace('-', ' ').toUpperCase()}\n`;
    alertMessage += `Date: ${event.start.toLocaleDateString()}\n`;

    if (extendedProps.assignedTo) {
      alertMessage += `Assigned to: ${extendedProps.assignedTo}\n`;
    }

    if (extendedProps.location) {
      alertMessage += `Location: ${extendedProps.location}\n`;
    }

    if (extendedProps.description) {
      alertMessage += `Description: ${extendedProps.description}`;
    }

    alert(alertMessage);
  };

  return (
    <div className="calendar-container">
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        headerToolbar={{
          left: 'prev,next today',
          center: 'title',
          right: 'dayGridMonth,timeGridWeek,timeGridDay'
        }}
        events={allEvents}
        eventClick={handleEventClick}
        height="500px"
        eventDisplay="block"
        dayMaxEvents={3}
        moreLinkClick="popover"
        weekends={true}
        businessHours={{
          daysOfWeek: [1, 2, 3, 4, 5], // Monday - Friday
          startTime: '08:00',
          endTime: '17:00',
        }}
        slotMinTime="07:00"
        slotMaxTime="19:00"
        nowIndicator={true}
        selectable={true}
        selectMirror={true}
        eventTimeFormat={{
          hour: 'numeric',
          minute: '2-digit',
          meridiem: 'short'
        }}
      />

      {/* Legend */}
      <div className="mt-3">
        <div className="row">
          <div className="col-12">
            <small className="text-muted fw-bold">Event Legend:</small>
