import { useState } from 'react';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { LEAD_ORIGINS, ASSIGNEES } from '@shared/schema';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface AddLeadModalProps {
  show: boolean;
  onHide: () => void;
}

export function AddLeadModal({ show, onHide }: AddLeadModalProps) {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
    lead_origin: '',
    assigned_to: '',
    project_amount: '',
    notes: ''
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const createLeadMutation = useMutation({
    mutationFn: async (leadData: any) => {
      const response = await apiRequest('POST', '/api/leads', leadData);
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Success", description: "Lead created successfully!" });
      queryClient.invalidateQueries({ queryKey: ['/api/leads'] });
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
      resetForm();
      onHide();
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create lead", variant: "destructive" });
    }
  });

  const resetForm = () => {
    setFormData({
      name: '',
      phone: '',
      email: '',
      lead_origin: '',
      assigned_to: '',
      project_amount: '',
      notes: ''
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Validate required fields
    if (!formData.name || !formData.phone || !formData.lead_origin || !formData.assigned_to) {
      toast({ title: "Error", description: "Please fill in all required fields", variant: "destructive" });
      return;
    }

    const leadData = {
      name: formData.name,
      phone: formData.phone,
      email: formData.email || null,
      lead_origin: formData.lead_origin,
      assigned_to: formData.assigned_to,
      project_amount: formData.project_amount ? parseFloat(formData.project_amount) : null,
      notes: formData.notes || null,
      remarks: "new", // Default status for new leads
      next_followup_date: null,
      additional_notes: null,
      deposit_paid: false,
      balance_paid: false,
      installation_date: null,
      assigned_installer: null
    };

    createLeadMutation.mutate(leadData);
  };

  return (
    <div 
      className={`modal fade ${show ? 'show' : ''}`} 
      style={{ display: show ? 'block' : 'none' }}
      data-testid="add-lead-modal"
    >
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Add New Lead</h5>
            <button 
              type="button" 
              className="btn-close" 
              onClick={onHide}
              data-testid="button-close-add-modal"
            ></button>
          </div>
          <div className="modal-body">
            <form onSubmit={handleSubmit}>
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label className="form-label">Name *</label>
                  <input
                    type="text"
                    className="form-control"
                    value={formData.name}
                    onChange={(e) => setFormData({...formData, name: e.target.value})}
                    required
                    data-testid="input-add-name"
                  />
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label">Phone *</label>
                  <input
                    type="tel"
                    className="form-control"
                    value={formData.phone}
                    onChange={(e) => setFormData({...formData, phone: e.target.value})}
                    required
                    data-testid="input-add-phone"
                  />
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label">Email</label>
                  <input
                    type="email"
                    className="form-control"
                    value={formData.email}
                    onChange={(e) => setFormData({...formData, email: e.target.value})}
                    data-testid="input-add-email"
                  />
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label">Lead Origin *</label>
                  <select
                    className="form-select"
                    value={formData.lead_origin}
                    onChange={(e) => setFormData({...formData, lead_origin: e.target.value})}
                    required
                    data-testid="select-add-origin"
                  >
                    <option value="">Select Origin</option>
                    {LEAD_ORIGINS.map(origin => (
                      <option key={origin} value={origin}>{origin}</option>
                    ))}
                  </select>
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label">Assigned To *</label>
                  <select
                    className="form-select"
                    value={formData.assigned_to}
                    onChange={(e) => setFormData({...formData, assigned_to: e.target.value})}
                    required
                    data-testid="select-add-assigned"
                  >
                    <option value="">Select Team Member</option>
                    {ASSIGNEES.map(assignee => (
                      <option key={assignee} value={assignee}>{assignee}</option>
                    ))}
                  </select>
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label">Project Amount</label>
                  <input
                    type="number"
                    className="form-control"
                    step="0.01"
                    value={formData.project_amount}
                    onChange={(e) => setFormData({...formData, project_amount: e.target.value})}
                    data-testid="input-add-amount"
                  />
                </div>
                <div className="col-12 mb-3">
                  <label className="form-label">Notes</label>
                  <textarea
                    className="form-control"
                    rows={3}
                    placeholder="Add any initial notes about this lead..."
                    value={formData.notes}
                    onChange={(e) => setFormData({...formData, notes: e.target.value})}
                    data-testid="textarea-add-notes"
                  />
                </div>
              </div>
            </form>
          </div>
          <div className="modal-footer">
            <button 
              type="button" 
              className="btn btn-secondary" 
              onClick={onHide}
              data-testid="button-cancel-add"
            >
              Cancel
            </button>
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleSubmit}
              disabled={createLeadMutation.isPending}
              data-testid="button-save-add"
            >
              {createLeadMutation.isPending ? (
                <>
                  <i className="fas fa-spinner fa-spin me-2"></i>Adding...
                </>
              ) : (
                'Add Lead'
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/modals/edit-bookle.content:
import { useState, useEffect } from 'react';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { SampleBooklet, UpdateSampleBooklet, updateSampleBookletSchema } from '@shared/schema';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface EditBookletModalProps {
  isOpen: boolean;
  onClose: () => void;
  booklet: SampleBooklet | null;
}

interface FormData extends Omit<UpdateSampleBooklet, 'date_shipped'> {
  date_shipped?: string;
}

export default function EditBookletModal({ isOpen, onClose, booklet }: EditBookletModalProps) {
  const [formData, setFormData] = useState<FormData>({});

  const { toast } = useToast();
  const queryClient = useQueryClient();

  useEffect(() => {
    if (booklet) {
      setFormData({
        order_number: booklet.order_number,
        customer_name: booklet.customer_name,
        address: booklet.address,
        email: booklet.email,
        phone: booklet.phone,
        product_type: booklet.product_type,
        status: booklet.status,
        tracking_number: booklet.tracking_number,
        date_shipped: booklet.date_shipped ? new Date(booklet.date_shipped).toISOString().split('T')[0] : '',
        notes: booklet.notes,
      });
    }
  }, [booklet]);

  const updateBookletMutation = useMutation({
    mutationFn: async (data: UpdateSampleBooklet) => {
      if (!booklet) throw new Error('No booklet selected');
      return await apiRequest('PUT', `/api/sample-booklets/${booklet.id}`, data);
    },
    onSuccess: () => {
      toast({ title: "Success", description: "Sample booklet order updated successfully!" });
      queryClient.invalidateQueries({ queryKey: ['/api/sample-booklets'] });
      queryClient.invalidateQueries({ queryKey: ['/api/sample-booklets/stats/dashboard'] });
      onClose();
    },
    onError: (error: any) => {
      console.error('Error updating booklet:', error);
      toast({ 
        title: "Error", 
        description: error.message || "Failed to update sample booklet order", 
        variant: "destructive" 
      });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const processedData: UpdateSampleBooklet = {
        ...formData,
        date_shipped: formData.date_shipped ? new Date(formData.date_shipped) : null
      };
      const validatedData = updateSampleBookletSchema.parse(processedData);
      updateBookletMutation.mutate(validatedData);
    } catch (error: any) {
      toast({ 
        title: "Validation Error", 
        description: "Please check all fields", 
        variant: "destructive" 
      });
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value || null
    }));
  };

  const handleStatusChange = (newStatus: string) => {
    setFormData(prev => ({
      ...prev,
      status: newStatus,
      date_shipped: newStatus === 'shipped' && !prev.date_shipped ? new Date().toISOString().split('T')[0] : prev.date_shipped
    }));
  };

  if (!isOpen || !booklet) return null;

  return (
    <div className="modal show" style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}>
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Edit Sample Booklet Order - {booklet.order_number}</h5>
            <button
              type="button"
              className="btn-close"
              onClick={onClose}
              data-testid="button-close-edit-modal"
            ></button>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="modal-body">
              <div className="row g-3">
                <div className="col-md-6">
                  <label className="form-label">Order Number</label>
                  <input
                    type="text"
                    className="form-control"
                    name="order_number"
                    value={formData.order_number || ''}
                    onChange={handleInputChange}
                    data-testid="input-edit-order-number"
                  />
                </div>

                <div className="col-md-6">
                  <label className="form-label">Product Type</label>
                  <select
                    className="form-select"
                    name="product_type"
                    value={formData.product_type || ''}
                    onChange={handleInputChange}
                    data-testid="select-edit-product-type"
                  >
                    <option value="demo_kit_and_sample_booklet">Demo Kit & Sample Booklet</option>
                    <option value="sample_booklet_only">Sample Booklet Only</option>
                    <option value="trial_kit">Trial Kit</option>
                    <option value="demo_kit_only">Demo Kit Only</option>
                  </select>
                </div>

                <div className="col-md-6">
                  <label className="form-label">Customer Name</label>
                  <input
                    type="text"
                    className="form-control"
                    name="customer_name"
                    value={formData.customer_name || ''}
                    onChange={handleInputChange}
                    data-testid="input-edit-customer-name"
                  />
                </div>

                <div className="col-md-6">
                  <label className="form-label">Phone</label>
                  <input
                    type="tel"
                    className="form-control"
                    name="phone"
                    value={formData.phone || ''}
                    onChange={handleInputChange}
                    data-testid="input-edit-phone"
                  />
                </div>

                <div className="col-md-6">
                  <label className="form-label">Email</label>
                  <input
                    type="email"
                    className="form-control"
                    name="email"
                    value={formData.email || ''}
                    onChange={handleInputChange}
                    data-testid="input-edit-email"
                  />
                </div>

                <div className="col-md-6">
                  <label className="form-label">Status</label>
                  <select
                    className="form-select"
                    name="status"
                    value={formData.status || 'pending'}
                    onChange={(e) => handleStatusChange(e.target.value)}
